INSERT INTO `menu` (`id`, `tenMenu`, `noiDung`) VALUES
(1, 'Giới Thiệu', '<p><strong>Salem Piano l&agrave; c&ocirc;ng ty chuy&ecirc;n nhập khẩu đ&agrave;n piano trực tiếp từ Nhật Bản. Ch&uacute;ng t&ocirc;i c&oacute; cửa h&agrave;ng lớn nhất tại Đ&agrave; Nẵng. C&aacute;c sản phẩm đ&agrave;n piano v&agrave; c&aacute;c nhạc cụ được kiểm định chất lượng tại Nhật trước khi vận chuyển v&agrave; được đ&oacute;ng g&oacute;i cẩn thận giữ nguy&ecirc;n hiện trạng khi về đến kho. Tất cả những c&acirc;y đ&agrave;n piano đều c&oacute; giấy chứng nhận xuất xứ v&agrave; kiểm định chất lượng do b&ecirc;n Nhật bản cung cấp. Do vậy, kh&aacute;ch h&agrave;ng mua đ&agrave;n piano ở Salem Piano ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sản phẩm chất lượng v&agrave; xuất xứ ch&iacute;nh h&atilde;ng&nbsp; ..</strong></p>\r\n\r\n<p><strong><img alt=\"salem\" src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/53110863_1025630377631843_3581716662862217216_n.jpg?_nc_cat=107&amp;_nc_oc=AQn5x4_iqvFkKaMkhD77fQG1ihnBsBdbtpinxNwQcg9ohHaqljJ_Dxzdu_YeGyixCYyyWInZyBHVKrEYHpc9H_qX&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=75ea1612f8ffcf4bcc82879c3640163f&amp;oe=5D1D53F6\" /></strong></p>\r\n\r\n<p><br />\r\n<strong>C&ocirc;ng ty b&aacute;n nhiều h&atilde;ng đ&agrave;n piano nổi tiếng của Nhật từ cổ điển đến những phi&ecirc;n bản mới nhất. Nếu bạn l&agrave; nhạc c&ocirc;ng hay am hiểu về đ&agrave;n piano, bạn c&oacute; thể đến trực tiếp cửa h&agrave;ng để trải nghiệm v&agrave; lựa chọn loại nhạc cụ ph&ugrave; hợp nhất. Nếu bạn l&agrave; người mới bắt đầu, bạn băn khoăn sản phẩm c&oacute; ph&ugrave; hợp với m&igrave;nh hay kh&ocirc;ng, h&atilde;y để nhạc c&ocirc;ng chuy&ecirc;n nghiệp của ch&uacute;ng t&ocirc;i tư vấn cho bạn</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"http://127.0.0.1:8000/public/upload/files/1sl.png\" style=\"height:412px; width:870px\" /></strong></p>\r\n\r\n<p><strong>B&ecirc;n cạnh b&aacute;n những c&acirc;y đ&agrave;n piano chất lượng, ch&uacute;ng t&ocirc;i c&oacute; trung t&acirc;m Đ&agrave;o Tạo m Nhạc uy t&iacute;n tại Đ&agrave; Nẵng - nơi ươm mầm v&agrave; th&aacute;ch thức những t&agrave;i năng &acirc;m nhạc. Ch&uacute;ng t&ocirc;i dạy chơi nhạc cụ, luyện thanh, đ&agrave;o tạo thi nhạc viện.</strong></p>\r\n\r\n<p><strong><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/33902502_1998818950338629_3964288508226437120_o.jpg?_nc_cat=106&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=bad32205426d1ce26ff6a1ce304a2dda&amp;oe=5CA12870\" /></strong></p>\r\n\r\n<p><br />\r\n<strong>Với phương ch&acirc;m &ldquo;chất lượng sản phẩm v&agrave; dịch vụ song h&agrave;nh&rdquo; ch&uacute;ng t&ocirc;i cam kết đem lại chất lượng dịch vụ tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng sản phẩm của ch&uacute;ng t&ocirc;i. Tất cả c&aacute;c c&acirc;y đ&agrave;n mua tại c&ocirc;ng ty Salem Piano sẽ được bảo h&agrave;nh trọn đời.</strong></p>\r\n\r\n<p><strong><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/17553863_1799245863629273_4933453990315646529_n.jpg?_nc_cat=105&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=a87eebbef4d531c04e2879d5e0736b4e&amp;oe=5CD62315\" /></strong></p>\r\n\r\n<p><br />\r\n<strong>Qu&yacute; kh&aacute;ch h&atilde;y gh&eacute; thăm cửa h&agrave;ng ch&uacute;ng t&ocirc;i để tận mắt thấy sản phẩm, trải nghiệm dịch vụ v&agrave; được tư vấn để mua được c&acirc;y đ&agrave;n piano ưng &yacute; nhất.</strong></p>\r\n\r\n<p><strong><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/51545826_1012960465565501_4543120778261954560_n.jpg?_nc_cat=105&amp;_nc_oc=AQk7jIKBOeUkJCcbNLkvgJ62zynrDwrYiAqO4Sjf3M7FoBJd3gQBuwMoG6A3Q0-3-xxgQ9l-mEgIWowFJiV5v8FJ&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=ebeae0bcc88dde7b492ad9e660fb130a&amp;oe=5D0A9C22\" /></strong></p>\r\n\r\n<p><br />\r\n<strong>Cửa h&agrave;ng Salem Piano cam kết đem đến cho kh&aacute;ch h&agrave;ng:</strong></p>\r\n\r\n<p><strong>H&agrave;ng h&oacute;a ch&iacute;nh h&atilde;ng, đảm bảo chất lượng</strong></p>\r\n\r\n<p><strong>Gi&aacute; h&agrave;ng ho&aacute; phải chăng</strong></p>\r\n\r\n<p><strong>Giao h&agrave;ng nhanh ch&oacute;ng, thuận tiện</strong></p>\r\n\r\n<p><strong>Đội ngũ nh&acirc;n vi&ecirc;n gi&agrave;u chuy&ecirc;n nghiệp, gi&agrave;u kinh nghiệm sẽ tư vấn những giải ph&aacute;p hợp l&yacute;, tối ưu, ph&ugrave; hợp nhu cầu của kh&aacute;ch h&agrave;ng</strong></p>\r\n\r\n<p><strong>Chế độ bảo tr&igrave;, bảo h&agrave;nh tiện lợi</strong></p>'),
(2, 'Giáo Dục', '<p>Kh&oacute;a học piano chất lượng v&agrave; hiệu quả nhất. Học đ&agrave;n piano đang trở th&agrave;nh xu hướng kh&aacute; phổ biến hiện nay, c&ugrave;ng với sự tăng trưởng của nền kinh tế, con người đ&atilde; dần quan t&acirc;m đến việc chăm lo đời sống tinh thần v&agrave; học piano được xem l&agrave; sự khởi đầu tốt nhất để l&agrave;m phong ph&uacute; đời sống tinh thần. V&agrave; để đ&aacute;p ứng nhu cầu học đ&agrave;n piano của những người y&ecirc;u nhạc, nhiều kh&oacute;a học piano được mở ra. Mặc d&ugrave; số lượng c&aacute;c kh&oacute;a học mở ra kh&aacute; nhiều, song chất lượng v&agrave; hiệu quả của từng kh&oacute;a học th&igrave; kh&ocirc;ng ai c&oacute; thể kiểm chứng được. Đ&acirc;y cũng ch&iacute;nh l&agrave; trở ngại lớn cho những người đ&atilde; v&agrave; đang t&igrave;m một kh&oacute;a học đ&agrave;n piano. Nếu đang bị bế tắc ở đ&acirc;y th&igrave; bạn n&ecirc;n d&agrave;nh thời gian t&igrave;m hiểu th&ecirc;m th&ocirc;ng tin về c&aacute;c kh&oacute;a học, tham khảo c&aacute;c đ&aacute;nh gi&aacute; của những người đ&atilde; tham gia học v&agrave; lựa chọn cho m&igrave;nh một kh&oacute;a học ph&ugrave; hợp nhất, cả về thời gian lẫn chi ph&iacute;.</p>\r\n\r\n<p><img src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/46384967_2125095051044351_8327041074035949568_o.jpg?_nc_cat=101&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=39d5843bbbb05151c9b168e4d20e44e2&amp;oe=5C8DB5D3\" /></p>\r\n\r\n<h3><br />\r\nTrường dạy nhạc Salem Piano &ndash; Địa điểm học đ&aacute;nh đ&agrave;n PIANO tốt nhất</h3>\r\n\r\n<p>Kh&oacute;a học piano chất lượng v&agrave; hiệu quả nhất. Học đ&agrave;n piano đang trở th&agrave;nh xu hướng kh&aacute; phổ biến hiện nay, c&ugrave;ng với Salem Piano được xem l&agrave; một gợi &yacute; kh&aacute; hay để bạn lựa chọn học piano. C&aacute;c kh&oacute;a học piano được mở li&ecirc;n tục, chương tr&igrave;nh học b&agrave;i bản theo từng cấp độ v&agrave; độ tuổi kh&aacute;c nhau. Gi&aacute;o vi&ecirc;n đứng lớp hầu hết đều l&agrave; những nghệ sĩ piano nổi tiếng v&agrave; c&oacute; bề d&agrave;y kinh nghiệm trong giảng dạy. Kh&ocirc;ng chỉ tập trung mang lại một hiệu quả học tập tốt nhất, Salem Piano c&ograve;n đưa ra một mức học ph&iacute; ưu đ&atilde;i, tạo điều kiện cho tất cả mọi người đều c&oacute; thể tham gia c&aacute;c kh&oacute;a học. Với những g&igrave; đem lại, th&igrave; đ&acirc;y ch&iacute;nh x&aacute;c l&agrave; một địa điểm học piano uy t&iacute;n, chất lượng v&agrave; hiệu quả.</p>\r\n\r\n<p><img alt=\"\" src=\"http://127.0.0.1:8000/public/upload/files/cfdsf.png\" style=\"height:544px; width:720px\" /></p>\r\n\r\n<h3>ĐỐI TƯỢNG HỌC</h3>\r\n\r\n<p>D&agrave;nh cho mọi đối tượng từ 3 tuổi trở l&ecirc;n, miễn l&agrave; c&oacute; niềm đam m&ecirc; với bộ m&ocirc;n Piano</p>\r\n\r\n<p>D&agrave;nh cho c&aacute;c bạn đang muốn n&acirc;ng cao kỹ năng đ&aacute;nh đ&agrave;n Piano</p>\r\n\r\n<p>D&agrave;nh cho c&aacute;c bạn muốn học th&ecirc;m 1 bộ m&ocirc;n nhạc cụ mới để trải nghiệm</p>\r\n\r\n<p>Học để luyện thi v&agrave;o c&aacute;c trường Nhạc viện</p>\r\n\r\n<p><img alt=\"\" src=\"http://127.0.0.1:8000/public/upload/files/53609153_1033445210183693_5146621944581324800_n.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<h3><br />\r\nCHẤT LƯỢNG Đ&Agrave;O TẠO TẠI SALEM PIANO</h3>\r\n\r\n<p>Học vi&ecirc;n được chọn khung giờ học cảm thấy thoải m&aacute;i nhất cho bản th&acirc;n</p>\r\n\r\n<p>Học theo y&ecirc;u cầu: Học vi&ecirc;n được quyền chọn lựa c&aacute;c kh&oacute;a học như Đệm đ&agrave;n Piano, Cơ bản, Cấp tốc, C&aacute;c kh&oacute;a học đ&agrave;n Piano trẻ em</p>\r\n\r\n<p>Đội ngũ giảng vi&ecirc;n tận t&acirc;m, với hơn 2.000 giờ dạy tại Trung t&acirc;m</p>\r\n\r\n<p>Tối thiểu +4 năm kinh nghiệm sư phạm &acirc;m nhạc, nhạc viện kinh nghiệm +5 năm tr&igrave;nh diễn Piano</p>\r\n\r\n<p>Chương tr&igrave;nh học c&oacute; lộ tr&igrave;nh cụ thể, cam kết 100% lộ tr&igrave;nh.</p>\r\n\r\n<p>SALEM PIANO l&agrave; tự h&agrave;o l&agrave; đơn vị đ&atilde; đ&agrave;o tạo tr&ecirc;n +3.000 học vi&ecirc;n</p>\r\n\r\n<p>Tư vấn v&agrave; Đăng k&yacute; học đ&agrave;n PIANO th&ocirc;ng qua HOTLINE: 039 469 0998</p>'),
(3, 'Dịch Vụ Sửa Chữa', '<p><strong>Dịch vụ sửa chữa đ&agrave;n piano&nbsp;- Ngo&agrave;i việc d&acirc;y đ&agrave;n piano xuống cấp theo thời gian, đ&agrave;n piano cũng vậy, c&aacute;c thiết bị kh&aacute;c của n&oacute; c&oacute; thể xảy ra lỗi, hỏng h&oacute;c do nhiều yếu tố kh&aacute;c nhau, c&oacute; thể l&agrave; kh&aacute;ch quan hoặc chủ quan, c&oacute; thể do qu&aacute; tr&igrave;nh sử dụng cũng c&oacute; thể do lỗi kỹ thuật của đ&agrave;n, v&igrave; thế ch&uacute;ng t&ocirc;i cung cấp dịch vụ sửa chữa đ&agrave;n piano tại nh&agrave; cho kh&aacute;ch h&agrave;ng</strong></p>\r\n\r\n<p><strong>C&aacute;c dịch vụ sửa chữa được Salem Piano&nbsp;cung cấp.</strong></p>\r\n\r\n<p><strong>1. Thay d&acirc;y đ&agrave;n piano:</strong></p>\r\n\r\n<p><strong>Đ&agrave;n Piano gồm 232 sợi d&acirc;y d&ugrave;ng cho cho 88 ph&iacute;m đ&agrave;n, nếu bạn&nbsp;sử dụng nhiều hoặc bảo quản đ&agrave;n kh&ocirc;ng tốt sẽ khiến cho d&acirc;y đ&agrave;n c&oacute; thể sẽ bị rỉ, đứt &nbsp;hay tởi vỏ bọc đồng sẽ l&agrave;m cho &acirc;m thanh kh&ocirc;ng đảm bảo v&agrave; ảnh hưởng trực tiếp đến tai nghe của người sử dụng. Với những lỗi n&agrave;y, th&igrave; bạn n&ecirc;n thay d&acirc;y v&igrave; l&ecirc;n d&acirc;y cũng sẽ kh&ocirc;ng hiệu quả.</strong></p>\r\n\r\n<p><strong>Ch&uacute;ng t&ocirc;i cam kết c&aacute;c loại d&acirc;y piano được thay v&agrave;o đều nhập khẩu từ c&aacute;c h&atilde;ng d&acirc;y đ&agrave;n piano của Nhật Bản, đảm bảo chất lượng cũng như &acirc;m thanh cho đ&agrave;n.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"http://127.0.0.1:8000/public/upload/files/Sua-chua-dan-Piano.jpg\" style=\"height:612px; width:816px\" /></strong></p>\r\n\r\n<p><strong>2. Vệ sinh b&agrave;n ph&iacute;m:</strong></p>\r\n\r\n<p><strong>B&agrave;n ph&iacute;m c&oacute; vấn đề l&agrave; lỗi phổ&nbsp;biến nhất trong qu&aacute; tr&igrave;nh sử dụng đ&agrave;n Piano. Đặc biết với m&ocirc;i trường kh&iacute; hậu nhiệt đới gi&oacute; m&ugrave;a n&ecirc;n độ ẩm rất cao do vậy lượng kh&iacute; ẩm thẩm thấu v&agrave;o c&aacute;c chi tiết m&aacute;y, l&agrave;m nở c&aacute;c chi tiết n&agrave;y v&agrave; với lượng bụi c&oacute; sẵn trong c&aacute;c chi tiết m&aacute;y l&agrave;m kẹt cứng khiến cho người sử dụng gặp kh&oacute; khăn trong việc chơi đ&agrave;n. Để giải quyết việc n&agrave;y n&ecirc;n đảm bảo giữ cho đ&agrave;n lu&ocirc;n ở trong m&ocirc;i trường sạch sẽ, c&oacute; độ ẩm khoảng 50-60%. Ch&uacute;ng t&ocirc;i giới thiệu đến bạn một số c&aacute;ch để phong tr&aacute;nh lỗi n&agrave;y cho đ&agrave;n piano.</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Bạn n&ecirc;n bảo dưởng, bảo tr&igrave; đ&agrave;n piano định kỳ 1 năm 1 lần.</strong></li>\r\n	<li><strong>Bảo quản piano trong m&ocirc;i trường tho&aacute;ng kh&iacute; hoặc trong ph&ograve;ng c&oacute; điều h&ograve;a</strong></li>\r\n	<li><strong>Kh&ocirc;ng đặt piano s&aacute;t tường m&agrave; n&ecirc;n để c&aacute;ch ra 5 &ndash; 10 cm để tr&aacute;nh ẩm.</strong></li>\r\n	<li><strong>Cắm ống sưởi cho đ&agrave;n khi độ ẩm tăng cao, cụ thể l&agrave; v&agrave;o c&aacute;c ng&agrave;y mưa, c&oacute; sương m&ugrave; hoặc v&agrave;o m&ugrave;a mưa xu&acirc;n. Lưu &yacute; căm ống sấy cũng n&ecirc;n đ&uacute;ng c&aacute;ch, c&aacute;c bạn kh&ocirc;ng n&ecirc;n qu&aacute; l&agrave;m dụng điều n&agrave;y v&igrave; độ ẩm qu&aacute; thấp cũng khiến gỗ bị kh&ocirc;, c&aacute;c thớ gỗ mất li&ecirc;n kết cơ học v&agrave; c&oacute; thể g&acirc;y nứt c&aacute;c chi tiết m&aacute;y.</strong></li>\r\n</ul>\r\n\r\n<p><strong>3. C&acirc;n chỉnh ph&iacute;m đ&agrave;n:&nbsp;&nbsp;Cũng tương tự như c&aacute;c chi tiết kh&aacute;c b&agrave;n ph&iacute;m sau thời gian d&agrave;i cũng mất đi sự ch&iacute;nh x&aacute;c vốn c&oacute; của n&oacute; thay v&agrave;o đ&oacute; l&agrave; t&igrave;nh trạng l&uacute;n, lắc v&agrave; mất ch&iacute;nh x&aacute;c khi chơi đ&agrave;n. Bạn n&ecirc;n nhớ Ph&iacute;m rất quan trọng v&igrave; n&oacute; đ&oacute;ng vai tr&ograve; ch&iacute;nh gi&uacute;p tạo cảm x&uacute;c cho người nghệ sỹ.</strong></p>\r\n\r\n<p><strong>4. Sửa chữa, thay thế b&uacute;a đ&agrave;n:&nbsp;B&uacute;a đ&agrave;n được l&agrave;m bằng l&ocirc;ng cừu &eacute;p li&ecirc;n kết với trục gỗ v&agrave; c&aacute;c chi tiết nhỏ, do vậy sau một thời gian d&agrave;i sử dụng, b&uacute;a đ&agrave;n sẽ xảy ra c&aacute;c trường hợp sau:</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Đầu b&uacute;a bị chai: B&uacute;a đ&agrave;n tạo ra &acirc;m thanh bằng c&aacute;ch đ&aacute;nh v&agrave;o d&acirc;y đ&agrave;n tuy nhi&ecirc;n n&oacute; c&acirc;n c&oacute; độ xốp nhất định để giữ cho &acirc;m thanh được trong v&agrave; ấm, sau một thời gian d&agrave;i sử dụng, với tần suất lực đ&aacute;nh lớn v&agrave; nhiều b&uacute;a đ&agrave;n sẽ tạo ra c&aacute;c r&atilde;nh tr&ecirc;n đầu b&uacute;a v&agrave; dần dần sẽ tạo ra sự chai ở c&aacute;c r&atilde;nh b&uacute;a n&agrave;y. Việc chai đầu b&uacute;a sẽ l&agrave;m cho b&uacute;a mất đi &acirc;m sắc ấm &aacute;p m&agrave; thay v&agrave;o đ&oacute; l&agrave; &acirc;m thanh đanh v&agrave; chua, tạo cảm gi&aacute;c kh&oacute; chịu cho người chơi v&agrave; giảm thời gian chơi li&ecirc;n tục trong thời gian d&agrave;i của người nghệ sỹ.</strong></li>\r\n	<li><strong>Trục b&uacute;a bị g&atilde;y:&nbsp;Việc n&agrave;y rất &iacute;t khi xảy ra, nhưng nếu đ&atilde; xảy ra th&igrave; bạn n&ecirc;n khắc phục sớm, v&igrave; b&uacute;a g&atilde;y rồi th&igrave; ph&iacute;m đ&agrave;n đ&oacute; bạn sẽ kh&ocirc;ng sử dụng được.</strong></li>\r\n	<li><strong>Đứt d&acirc;y giật v&agrave; d&acirc;y h&atilde;m b&uacute;a</strong></li>\r\n	<li><strong>Chuột cắn</strong></li>\r\n</ul>\r\n\r\n<p><strong>5. Thay tấm nỉ giảm &acirc;m:&nbsp;C&oacute; nhiều t&aacute;c nh&acirc;n g&acirc;y hỏng nỉ giảm &acirc;m:</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Qu&aacute; tr&igrave;nh sử dụng d&agrave;i g&acirc;y r&aacute;ch, mục</strong></li>\r\n	<li><strong>Chuột cắn.</strong></li>\r\n	<li><strong>T&aacute;c nh&acirc;n kh&aacute;c.</strong></li>\r\n</ul>\r\n\r\n<p><strong>6. Căn chỉnh lại pedal:&nbsp;Qu&aacute; tr&igrave;nh chơi những bản nhạc do chủ &yacute; của t&aacute;c giả hoặc do c&aacute;ch chuyển hợp &acirc;m trong t&aacute;c phẩm qua thời gian d&agrave;i li&ecirc;n tục l&agrave;m c&aacute;c bulong bị tr&ocirc;i ngược khiến cho Pedal bị rung lắc. Điều n&agrave;y c&oacute; thể dẫn đến g&atilde;y Pedal hoặc sự kh&oacute; chịu cho người chơi.</strong></p>\r\n\r\n<p><strong>7. Thay l&ograve; xo cho bộ m&aacute;y của đ&agrave;n piano&nbsp;:&nbsp;Một bộ m&aacute;y đ&agrave;n c&oacute; l&ograve; xo tốt sẽ tạo ra sự bật nảy đảm bảo cho người nghệ sỹ thăng hoa tr&ecirc;n c&acirc;y đ&agrave;n của họ</strong></p>\r\n\r\n<p><strong>8. Bảo dưỡng bộ m&aacute;y:&nbsp;Bộ m&aacute;y hoạt động l&acirc;u chắc chắn sẽ c&oacute; vấn đề, bạn cần kiểm tra, bảo dưỡng để bộ m&aacute;y c&oacute; thể hoạt động tốt nhất</strong></p>'),
(4, 'Tổ Chức Sự Kiện', '<p><strong>Tổ chức sự kiện</strong>&nbsp;th&agrave;nh c&ocirc;ng lu&ocirc;n mang lại nhiều lợi &iacute;ch cho doanh nghiệp, ngo&agrave;i việc quảng b&aacute; h&igrave;nh ảnh sản phẩm, dịch vụ v&agrave; thương hiệu, doanh nghiệp sẽ c&oacute; cơ hội tiếp cận gần hơn với kh&aacute;ch h&agrave;ng mục ti&ecirc;u v&agrave; đối t&aacute;c chiến lược.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://127.0.0.1:8000/public/upload/files/53513182_1025630427631838_5959885525476179968_n.jpg\" style=\"height:960px; width:719px\" /></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Salem piano tổ chức sự kiện h&agrave;ng đầu việt nam</em></p>\r\n\r\n<p><strong>Bạn đang t&igrave;m c&ocirc;ng ty tổ chức sự kiện chuy&ecirc;n nghiệp?</strong></p>\r\n\r\n<p>Theo khảo s&aacute;t của&nbsp;<strong>Salem Piano</strong>, c&oacute; khoảng 80% doanh nghiệp chưa h&agrave;i l&ograve;ng 100% v&agrave;o kết quả chương tr&igrave;nh sự kiện do c&aacute;c đơn vị tự tổ chức. Những kh&oacute; khăn chung m&agrave; c&aacute;c đơn vị tự tổ chức sự kiện thường hay gặp phải đ&oacute; l&agrave;:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Thiếu kế hoạch tổ chức sự kiện chi tiết</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - &Yacute; tưởng tổ chức sự kiện thiếu s&aacute;ng tạo</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Qu&aacute; nhiều c&aacute;c dịch vụ ph&aacute;t sinh: trang phục, &acirc;m thanh, đạo cụ, địa điểm phục vụ sự kiện.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Chi ph&iacute; ph&aacute;t sinh ch&ecirc;nh nhiều so với ng&acirc;n s&aacute;ch dự kiến ban đầu</p>\r\n\r\n<p><strong>Salem Piano&ndash; Giải ph&aacute;p tổ chức sự kiện chuy&ecirc;n nghiệp</strong></p>\r\n