INSERT INTO `menu` (`id`, `tenMenu`, `noiDung`) VALUES
(1, 'Giới Thiệu', '<p>Salem Piano l&agrave; c&ocirc;ng ty chuy&ecirc;n nhập khẩu đ&agrave;n piano trực tiếp từ Nhật Bản. Ch&uacute;ng t&ocirc;i c&oacute; cửa h&agrave;ng lớn nhất tại Đ&agrave; Nẵng. C&aacute;c sản phẩm đ&agrave;n piano v&agrave; c&aacute;c nhạc cụ được kiểm định chất lượng tại Nhật trước khi vận chuyển v&agrave; được đ&oacute;ng g&oacute;i cẩn thận giữ nguy&ecirc;n hiện trạng khi về đến kho. Tất cả những c&acirc;y đ&agrave;n piano đều c&oacute; giấy chứng nhận xuất xứ v&agrave; kiểm định chất lượng do b&ecirc;n Nhật bản cung cấp. Do vậy, kh&aacute;ch h&agrave;ng mua đ&agrave;n piano ở Salem Piano ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sản phẩm chất lượng v&agrave; xuất xứ ch&iacute;nh h&atilde;ng.</p>\r\n\r\n<p><img alt=\"salem\" src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/53110863_1025630377631843_3581716662862217216_n.jpg?_nc_cat=107&amp;_nc_oc=AQn5x4_iqvFkKaMkhD77fQG1ihnBsBdbtpinxNwQcg9ohHaqljJ_Dxzdu_YeGyixCYyyWInZyBHVKrEYHpc9H_qX&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=75ea1612f8ffcf4bcc82879c3640163f&amp;oe=5D1D53F6\" /></p>\r\n\r\n<p><br />\r\nC&ocirc;ng ty b&aacute;n nhiều h&atilde;ng đ&agrave;n piano nổi tiếng của Nhật từ cổ điển đến những phi&ecirc;n bản mới nhất. Nếu bạn l&agrave; nhạc c&ocirc;ng hay am hiểu về đ&agrave;n piano, bạn c&oacute; thể đến trực tiếp cửa h&agrave;ng để trải nghiệm v&agrave; lựa chọn loại nhạc cụ ph&ugrave; hợp nhất. Nếu bạn l&agrave; người mới bắt đầu, bạn băn khoăn sản phẩm c&oacute; ph&ugrave; hợp với m&igrave;nh hay kh&ocirc;ng, h&atilde;y để nhạc c&ocirc;ng chuy&ecirc;n nghiệp của ch&uacute;ng t&ocirc;i tư vấn cho bạn</p>\r\n\r\n<p>B&ecirc;n cạnh b&aacute;n những c&acirc;y đ&agrave;n piano chất lượng, ch&uacute;ng t&ocirc;i c&oacute; trung t&acirc;m Đ&agrave;o Tạo m Nhạc uy t&iacute;n tại Đ&agrave; Nẵng - nơi ươm mầm v&agrave; th&aacute;ch thức những t&agrave;i năng &acirc;m nhạc. Ch&uacute;ng t&ocirc;i dạy chơi nhạc cụ, luyện thanh, đ&agrave;o tạo thi nhạc viện.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/33902502_1998818950338629_3964288508226437120_o.jpg?_nc_cat=106&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=bad32205426d1ce26ff6a1ce304a2dda&amp;oe=5CA12870\" /></p>\r\n\r\n<p><br />\r\nVới phương ch&acirc;m &ldquo;chất lượng sản phẩm v&agrave; dịch vụ song h&agrave;nh&rdquo; ch&uacute;ng t&ocirc;i cam kết đem lại chất lượng dịch vụ tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng sản phẩm của ch&uacute;ng t&ocirc;i. Tất cả c&aacute;c c&acirc;y đ&agrave;n mua tại c&ocirc;ng ty Salem Piano sẽ được bảo h&agrave;nh trọn đời.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/17553863_1799245863629273_4933453990315646529_n.jpg?_nc_cat=105&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=a87eebbef4d531c04e2879d5e0736b4e&amp;oe=5CD62315\" /></p>\r\n\r\n<p><br />\r\nQu&yacute; kh&aacute;ch h&atilde;y gh&eacute; thăm cửa h&agrave;ng ch&uacute;ng t&ocirc;i để tận mắt thấy sản phẩm, trải nghiệm dịch vụ v&agrave; được tư vấn để mua được c&acirc;y đ&agrave;n piano ưng &yacute; nhất.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/51545826_1012960465565501_4543120778261954560_n.jpg?_nc_cat=105&amp;_nc_oc=AQk7jIKBOeUkJCcbNLkvgJ62zynrDwrYiAqO4Sjf3M7FoBJd3gQBuwMoG6A3Q0-3-xxgQ9l-mEgIWowFJiV5v8FJ&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=ebeae0bcc88dde7b492ad9e660fb130a&amp;oe=5D0A9C22\" /></p>\r\n\r\n<p><br />\r\nCửa h&agrave;ng Salem Piano cam kết đem đến cho kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<p>H&agrave;ng h&oacute;a ch&iacute;nh h&atilde;ng, đảm bảo chất lượng</p>\r\n\r\n<p>Gi&aacute; h&agrave;ng ho&aacute; phải chăng</p>\r\n\r\n<p>Giao h&agrave;ng nhanh ch&oacute;ng, thuận tiện</p>\r\n\r\n<p>Đội ngũ nh&acirc;n vi&ecirc;n gi&agrave;u chuy&ecirc;n nghiệp, gi&agrave;u kinh nghiệm sẽ tư vấn những giải ph&aacute;p hợp l&yacute;, tối ưu, ph&ugrave; hợp nhu cầu của kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Chế độ bảo tr&igrave;, bảo h&agrave;nh tiện lợi</p>'),
(2, 'Giáo Dục', '<p>Kh&oacute;a học piano chất lượng v&agrave; hiệu quả nhất. Học đ&agrave;n piano đang trở th&agrave;nh xu hướng kh&aacute; phổ biến hiện nay, c&ugrave;ng với sự tăng trưởng của nền kinh tế, con người đ&atilde; dần quan t&acirc;m đến việc chăm lo đời sống tinh thần v&agrave; học piano được xem l&agrave; sự khởi đầu tốt nhất để l&agrave;m phong ph&uacute; đời sống tinh thần. V&agrave; để đ&aacute;p ứng nhu cầu học đ&agrave;n piano của những người y&ecirc;u nhạc, nhiều kh&oacute;a học piano được mở ra. Mặc d&ugrave; số lượng c&aacute;c kh&oacute;a học mở ra kh&aacute; nhiều, song chất lượng v&agrave; hiệu quả của từng kh&oacute;a học th&igrave; kh&ocirc;ng ai c&oacute; thể kiểm chứng được. Đ&acirc;y cũng ch&iacute;nh l&agrave; trở ngại lớn cho những người đ&atilde; v&agrave; đang t&igrave;m một kh&oacute;a học đ&agrave;n piano. Nếu đang bị bế tắc ở đ&acirc;y th&igrave; bạn n&ecirc;n d&agrave;nh thời gian t&igrave;m hiểu th&ecirc;m th&ocirc;ng tin về c&aacute;c kh&oacute;a học, tham khảo c&aacute;c đ&aacute;nh gi&aacute; của những người đ&atilde; tham gia học v&agrave; lựa chọn cho m&igrave;nh một kh&oacute;a học ph&ugrave; hợp nhất, cả về thời gian lẫn chi ph&iacute;.</p>\r\n\r\n<p><img src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/46384967_2125095051044351_8327041074035949568_o.jpg?_nc_cat=101&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=39d5843bbbb05151c9b168e4d20e44e2&amp;oe=5C8DB5D3\" /></p>\r\n\r\n<h3><br />\r\nTrường dạy nhạc Salem Piano &ndash; Địa điểm học đ&aacute;nh đ&agrave;n PIANO tốt nhất</h3>\r\n\r\n<p>Kh&oacute;a học piano chất lượng v&agrave; hiệu quả nhất. Học đ&agrave;n piano đang trở th&agrave;nh xu hướng kh&aacute; phổ biến hiện nay, c&ugrave;ng với Salem Piano được xem l&agrave; một gợi &yacute; kh&aacute; hay để bạn lựa chọn học piano. C&aacute;c kh&oacute;a học piano được mở li&ecirc;n tục, chương tr&igrave;nh học b&agrave;i bản theo từng cấp độ v&agrave; độ tuổi kh&aacute;c nhau. Gi&aacute;o vi&ecirc;n đứng lớp hầu hết đều l&agrave; những nghệ sĩ piano nổi tiếng v&agrave; c&oacute; bề d&agrave;y kinh nghiệm trong giảng dạy. Kh&ocirc;ng chỉ tập trung mang lại một hiệu quả học tập tốt nhất, Salem Piano c&ograve;n đưa ra một mức học ph&iacute; ưu đ&atilde;i, tạo điều kiện cho tất cả mọi người đều c&oacute; thể tham gia c&aacute;c kh&oacute;a học. Với những g&igrave; đem lại, th&igrave; đ&acirc;y ch&iacute;nh x&aacute;c l&agrave; một địa điểm học piano uy t&iacute;n, chất lượng v&agrave; hiệu quả.</p>\r\n\r\n<h3>ĐỐI TƯỢNG HỌC</h3>\r\n\r\n<p>D&agrave;nh cho mọi đối tượng từ 3 tuổi trở l&ecirc;n, miễn l&agrave; c&oacute; niềm đam m&ecirc; với bộ m&ocirc;n Piano</p>\r\n\r\n<p>D&agrave;nh cho c&aacute;c bạn đang muốn n&acirc;ng cao kỹ năng đ&aacute;nh đ&agrave;n Piano</p>\r\n\r\n<p>D&agrave;nh cho c&aacute;c bạn muốn học th&ecirc;m 1 bộ m&ocirc;n nhạc cụ mới để trải nghiệm</p>\r\n\r\n<p>Học để luyện thi v&agrave;o c&aacute;c trường Nhạc viện</p>\r\n\r\n<p><img src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/44584979_2109690142584842_8126795267606315008_n.jpg?_nc_cat=107&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=6c8e102746a1d43a5d66f5d8a4147427&amp;oe=5C8DB6CB\" /></p>\r\n\r\n<h3><br />\r\nCHẤT LƯỢNG Đ&Agrave;O TẠO TẠI SALEM PIANO</h3>\r\n\r\n<p>Học vi&ecirc;n được chọn khung giờ học cảm thấy thoải m&aacute;i nhất cho bản th&acirc;n</p>\r\n\r\n<p>Học theo y&ecirc;u cầu: Học vi&ecirc;n được quyền chọn lựa c&aacute;c kh&oacute;a học như Đệm đ&agrave;n Piano, Cơ bản, Cấp tốc, C&aacute;c kh&oacute;a học đ&agrave;n Piano trẻ em</p>\r\n\r\n<p>Đội ngũ giảng vi&ecirc;n tận t&acirc;m, với hơn 2.000 giờ dạy tại Trung t&acirc;m</p>\r\n\r\n<p>Tối thiểu +4 năm kinh nghiệm sư phạm &acirc;m nhạc, nhạc viện kinh nghiệm +5 năm tr&igrave;nh diễn Piano</p>\r\n\r\n<p>Chương tr&igrave;nh học c&oacute; lộ tr&igrave;nh cụ thể, cam kết 100% lộ tr&igrave;nh.</p>\r\n\r\n<p>SALEM PIANO l&agrave; tự h&agrave;o l&agrave; đơn vị đ&atilde; đ&agrave;o tạo tr&ecirc;n +3.000 học vi&ecirc;n</p>\r\n\r\n<p>Tư vấn v&agrave; Đăng k&yacute; học đ&agrave;n PIANO th&ocirc;ng qua HOTLINE: 039 469 0998</p>'),
(3, 'Dịch Vụ Sửa Chữa', '<p>Salem Piano l&agrave; c&ocirc;ng ty chuy&ecirc;n nhập khẩu đ&agrave;n piano trực tiếp từ Nhật Bản. Ch&uacute;ng t&ocirc;i c&oacute; cửa h&agrave;ng lớn nhất tại Đ&agrave; Nẵng. C&aacute;c sản phẩm đ&agrave;n piano v&agrave; c&aacute;c nhạc cụ được kiểm định chất lượng tại Nhật trước khi vận chuyển v&agrave; được đ&oacute;ng g&oacute;i cẩn thận giữ nguy&ecirc;n hiện trạng khi về đến kho. Tất cả những c&acirc;y đ&agrave;n piano đều c&oacute; giấy chứng nhận xuất xứ v&agrave; kiểm định chất lượng do b&ecirc;n Nhật bản cung cấp. Do vậy, kh&aacute;ch h&agrave;ng mua đ&agrave;n piano ở Salem Piano ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sản phẩm chất lượng v&agrave; xuất xứ ch&iacute;nh h&atilde;ng.</p>\r\n\r\n<p><img alt=\"salem\" src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/53110863_1025630377631843_3581716662862217216_n.jpg?_nc_cat=107&amp;_nc_oc=AQn5x4_iqvFkKaMkhD77fQG1ihnBsBdbtpinxNwQcg9ohHaqljJ_Dxzdu_YeGyixCYyyWInZyBHVKrEYHpc9H_qX&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=75ea1612f8ffcf4bcc82879c3640163f&amp;oe=5D1D53F6\" /></p>\r\n\r\n<p><br />\r\nC&ocirc;ng ty b&aacute;n nhiều h&atilde;ng đ&agrave;n piano nổi tiếng của Nhật từ cổ điển đến những phi&ecirc;n bản mới nhất. Nếu bạn l&agrave; nhạc c&ocirc;ng hay am hiểu về đ&agrave;n piano, bạn c&oacute; thể đến trực tiếp cửa h&agrave;ng để trải nghiệm v&agrave; lựa chọn loại nhạc cụ ph&ugrave; hợp nhất. Nếu bạn l&agrave; người mới bắt đầu, bạn băn khoăn sản phẩm c&oacute; ph&ugrave; hợp với m&igrave;nh hay kh&ocirc;ng, h&atilde;y để nhạc c&ocirc;ng chuy&ecirc;n nghiệp của ch&uacute;ng t&ocirc;i tư vấn cho bạn</p>\r\n\r\n<p>B&ecirc;n cạnh b&aacute;n những c&acirc;y đ&agrave;n piano chất lượng, ch&uacute;ng t&ocirc;i c&oacute; trung t&acirc;m Đ&agrave;o Tạo m Nhạc uy t&iacute;n tại Đ&agrave; Nẵng - nơi ươm mầm v&agrave; th&aacute;ch thức những t&agrave;i năng &acirc;m nhạc. Ch&uacute;ng t&ocirc;i dạy chơi nhạc cụ, luyện thanh, đ&agrave;o tạo thi nhạc viện.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/33902502_1998818950338629_3964288508226437120_o.jpg?_nc_cat=106&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=bad32205426d1ce26ff6a1ce304a2dda&amp;oe=5CA12870\" /></p>\r\n\r\n<p><br />\r\nVới phương ch&acirc;m &ldquo;chất lượng sản phẩm v&agrave; dịch vụ song h&agrave;nh&rdquo; ch&uacute;ng t&ocirc;i cam kết đem lại chất lượng dịch vụ tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng sản phẩm của ch&uacute;ng t&ocirc;i. Tất cả c&aacute;c c&acirc;y đ&agrave;n mua tại c&ocirc;ng ty Salem Piano sẽ được bảo h&agrave;nh trọn đời.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/17553863_1799245863629273_4933453990315646529_n.jpg?_nc_cat=105&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=a87eebbef4d531c04e2879d5e0736b4e&amp;oe=5CD62315\" /></p>\r\n\r\n<p><br />\r\nQu&yacute; kh&aacute;ch h&atilde;y gh&eacute; thăm cửa h&agrave;ng ch&uacute;ng t&ocirc;i để tận mắt thấy sản phẩm, trải nghiệm dịch vụ v&agrave; được tư vấn để mua được c&acirc;y đ&agrave;n piano ưng &yacute; nhất.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/51545826_1012960465565501_4543120778261954560_n.jpg?_nc_cat=105&amp;_nc_oc=AQk7jIKBOeUkJCcbNLkvgJ62zynrDwrYiAqO4Sjf3M7FoBJd3gQBuwMoG6A3Q0-3-xxgQ9l-mEgIWowFJiV5v8FJ&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=ebeae0bcc88dde7b492ad9e660fb130a&amp;oe=5D0A9C22\" /></p>\r\n\r\n<p><br />\r\nCửa h&agrave;ng Salem Piano cam kết đem đến cho kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<p>H&agrave;ng h&oacute;a ch&iacute;nh h&atilde;ng, đảm bảo chất lượng</p>\r\n\r\n<p>Gi&aacute; h&agrave;ng ho&aacute; phải chăng</p>\r\n\r\n<p>Giao h&agrave;ng nhanh ch&oacute;ng, thuận tiện</p>\r\n\r\n<p>Đội ngũ nh&acirc;n vi&ecirc;n gi&agrave;u chuy&ecirc;n nghiệp, gi&agrave;u kinh nghiệm sẽ tư vấn những giải ph&aacute;p hợp l&yacute;, tối ưu, ph&ugrave; hợp nhu cầu của kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Chế độ bảo tr&igrave;, bảo h&agrave;nh tiện lợi</p>'),
(4, 'Tổ Chức Sự Kiện', '<p>Salem Piano l&agrave; c&ocirc;ng ty chuy&ecirc;n nhập khẩu đ&agrave;n piano trực tiếp từ Nhật Bản. Ch&uacute;ng t&ocirc;i c&oacute; cửa h&agrave;ng lớn nhất tại Đ&agrave; Nẵng. C&aacute;c sản phẩm đ&agrave;n piano v&agrave; c&aacute;c nhạc cụ được kiểm định chất lượng tại Nhật trước khi vận chuyển v&agrave; được đ&oacute;ng g&oacute;i cẩn thận giữ nguy&ecirc;n hiện trạng khi về đến kho. Tất cả những c&acirc;y đ&agrave;n piano đều c&oacute; giấy chứng nhận xuất xứ v&agrave; kiểm định chất lượng do b&ecirc;n Nhật bản cung cấp. Do vậy, kh&aacute;ch h&agrave;ng mua đ&agrave;n piano ở Salem Piano ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sản phẩm chất lượng v&agrave; xuất xứ ch&iacute;nh h&atilde;ng.</p>\r\n\r\n<p><img alt=\"salem\" src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/53110863_1025630377631843_3581716662862217216_n.jpg?_nc_cat=107&amp;_nc_oc=AQn5x4_iqvFkKaMkhD77fQG1ihnBsBdbtpinxNwQcg9ohHaqljJ_Dxzdu_YeGyixCYyyWInZyBHVKrEYHpc9H_qX&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=75ea1612f8ffcf4bcc82879c3640163f&amp;oe=5D1D53F6\" /></p>\r\n\r\n<p><br />\r\nC&ocirc;ng ty b&aacute;n nhiều h&atilde;ng đ&agrave;n piano nổi tiếng của Nhật từ cổ điển đến những phi&ecirc;n bản mới nhất. Nếu bạn l&agrave; nhạc c&ocirc;ng hay am hiểu về đ&agrave;n piano, bạn c&oacute; thể đến trực tiếp cửa h&agrave;ng để trải nghiệm v&agrave; lựa chọn loại nhạc cụ ph&ugrave; hợp nhất. Nếu bạn l&agrave; người mới bắt đầu, bạn băn khoăn sản phẩm c&oacute; ph&ugrave; hợp với m&igrave;nh hay kh&ocirc;ng, h&atilde;y để nhạc c&ocirc;ng chuy&ecirc;n nghiệp của ch&uacute;ng t&ocirc;i tư vấn cho bạn</p>\r\n\r\n<p>B&ecirc;n cạnh b&aacute;n những c&acirc;y đ&agrave;n piano chất lượng, ch&uacute;ng t&ocirc;i c&oacute; trung t&acirc;m Đ&agrave;o Tạo m Nhạc uy t&iacute;n tại Đ&agrave; Nẵng - nơi ươm mầm v&agrave; th&aacute;ch thức những t&agrave;i năng &acirc;m nhạc. Ch&uacute;ng t&ocirc;i dạy chơi nhạc cụ, luyện thanh, đ&agrave;o tạo thi nhạc viện.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/33902502_1998818950338629_3964288508226437120_o.jpg?_nc_cat=106&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=bad32205426d1ce26ff6a1ce304a2dda&amp;oe=5CA12870\" /></p>\r\n\r\n<p><br />\r\nVới phương ch&acirc;m &ldquo;chất lượng sản phẩm v&agrave; dịch vụ song h&agrave;nh&rdquo; ch&uacute;ng t&ocirc;i cam kết đem lại chất lượng dịch vụ tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng sản phẩm của ch&uacute;ng t&ocirc;i. Tất cả c&aacute;c c&acirc;y đ&agrave;n mua tại c&ocirc;ng ty Salem Piano sẽ được bảo h&agrave;nh trọn đời.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/17553863_1799245863629273_4933453990315646529_n.jpg?_nc_cat=105&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=a87eebbef4d531c04e2879d5e0736b4e&amp;oe=5CD62315\" /></p>\r\n\r\n<p><br />\r\nQu&yacute; kh&aacute;ch h&atilde;y gh&eacute; thăm cửa h&agrave;ng ch&uacute;ng t&ocirc;i để tận mắt thấy sản phẩm, trải nghiệm dịch vụ v&agrave; được tư vấn để mua được c&acirc;y đ&agrave;n piano ưng &yacute; nhất.</p>\r\n\r\n<p><img src=\"https://scontent.fdad1-1.fna.fbcdn.net/v/t1.0-9/51545826_1012960465565501_4543120778261954560_n.jpg?_nc_cat=105&amp;_nc_oc=AQk7jIKBOeUkJCcbNLkvgJ62zynrDwrYiAqO4Sjf3M7FoBJd3gQBuwMoG6A3Q0-3-xxgQ9l-mEgIWowFJiV5v8FJ&amp;_nc_ht=scontent.fdad1-1.fna&amp;oh=ebeae0bcc88dde7b492ad9e660fb130a&amp;oe=5D0A9C22\" /></p>\r\n\r\n<p><br />\r\nCửa h&agrave;ng Salem Piano cam kết đem đến cho kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<p>H&agrave;ng h&oacute;a ch&iacute;nh h&atilde;ng, đảm bảo chất lượng</p>\r\n\r\n<p>Gi&aacute; h&agrave;ng ho&aacute; phải chăng</p>\r\n\r\n<p>Giao h&agrave;ng nhanh ch&oacute;ng, thuận tiện</p>\r\n\r\n<p>Đội ngũ nh&acirc;n vi&ecirc;n gi&agrave;u chuy&ecirc;n nghiệp, gi&agrave;u kinh nghiệm sẽ tư vấn những giải ph&aacute;p hợp l&yacute;, tối ưu, ph&ugrave; hợp nhu cầu của kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Chế độ bảo tr&igrave;, bảo h&agrave;nh tiện lợi</p>'),
(5, 'Cho Thuê Nhạc Cụ', '<p>Salem Piano l&agrave; c&ocirc;ng ty chuy&ecirc;n nhập khẩu đ&agrave;n piano trực tiếp từ Nhật Bản. Ch&uacute;ng t&ocirc;i c&oacute; cửa h&agrave;ng lớn nhất tại Đ&agrave; Nẵng. C&aacute;c sản phẩm đ&agrave;n piano v&agrave; c&aacute;c nhạc cụ được kiểm định chất lượng tại Nhật trước khi vận chuyển v&agrave; được đ&oacute;ng g&oacute;i cẩn thận giữ nguy&ecirc;n hiện trạng khi về đến kho. Tất cả những c&acirc;y đ&agrave;n piano đều c&oacute; giấy chứng nhận xuất xứ v&agrave; kiểm định chất lượng do b&ecirc;n Nhật bản cung cấp. Do vậy, kh&aacute;ch h&agrave;ng mua đ&agrave;n piano ở Salem Piano ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sản phẩm chất lượng v&agrave; xuất xứ ch&iacute;nh h&atilde;ng.</p>\r\n\r\n<p><img alt=\"salem\" src=\"https://scontent.fdad2-1.fna.fbcdn.net/v/t1.0-9/53110863_1025630377631843_3581716662862217216_n.jpg?_nc_cat=107&amp;_nc_oc=AQn5x4_iqvFkKaMkhD77fQG1ihnBsBdbtpinxNwQcg9ohHaqljJ_Dxzdu_YeGyixCYyyWInZyBHVKrEYHpc9H_qX&amp;_nc_ht=scontent.fdad2-1.fna&amp;oh=75ea1612f8ffcf4bcc82879c3640163f&amp;oe=5D1D53F6\" /></p>\r\n\r\n<p><br />\r\nC&ocirc;ng ty b&aacute;n nhiều h&atilde;ng đ&agrave;n piano nổi tiếng của Nhật từ cổ điển đến những phi&ecirc;n bản mới nhất. Nếu bạn l&agrave; nhạc c&ocirc;ng hay am hiểu về đ&agrave;n piano, bạn c&oacute; thể đến trực tiếp cửa h&agrave;ng để trải nghiệm v&agrave; lựa chọn loại nhạc cụ ph&ugrave; hợp nhất. Nếu bạn l&agrave; người mới bắt đầu, bạn băn khoăn sản phẩm c&oacute; ph&ugrave; hợp với m&igrave;nh hay kh&ocirc;ng, h&atilde;y để nhạc c&ocirc;ng chuy&ecirc;n nghiệp của ch&uacute;ng t&ocirc;i tư vấn cho bạn</p>\r\n\r\n<p>B&ecirc;n cạnh b&aacute;n những c&acirc;y đ&agrave;n piano chất lượng, ch&uacute;ng t&ocirc;i c&oacute; trung t&acirc;m Đ&agrave;o Tạo m Nhạc uy t&iacute;n tại Đ&agrave; Nẵng - nơi ươm mầm v&agrave; th&aacute;ch thức những t&agrave;i năng &acirc;m nhạc. Ch&uacute;ng t&ocirc;i dạy chơi nhạc cụ, luyện thanh, đ&agrave;o tạo thi nhạc viện.</p>\r\n\r\n<p><img src=