INSERT INTO `sanpham` (`id`, `tenSP`, `gia`, `img`, `tomTat`, `MTSP`, `TSKT`, `maSP`) VALUES
(7, 'Cordoba C5', 6000000, 'usCggv3MvNtQgrTRQZRRyfVCnZ0hcSGfULD6xFCP.png', 'Cordoba C5 được thiết kế theo phong cách Tây Ban Nha với trọng lượng nhẹ được nhiều người yêu thích Mặt đàn : Gỗ tuyết tùng Canada rắn Mặt sau và mặt bên : Mahogany (gỗ)', '<p>Cordoba C5 được thiết kế&nbsp;tinh tế, sang trọng, đẹp mắt với chất liệu cao cấp&nbsp;mang đến &acirc;m thang trong trẻo nhưng cũng kh&ocirc;ng k&eacute;m phần ấm &aacute;p.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/cordoba/cordoba-cc5.jpg\" /></p>', '<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Thương hiệu</td>\r\n			<td>Cordoba</td>\r\n		</tr>\r\n		<tr>\r\n			<td>M&agrave;u/Mẫu</td>\r\n			<td>N&acirc;u</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xuất xứ thương hiệu</td>\r\n			<td>T&acirc;y Ban Nha</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sản xuất tại</td>\r\n			<td>Trung Quốc</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Model</td>\r\n			<td>C5</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 2),
(8, 'Kawai GL-20', 20000000, 'gVxv74LCqSEinatomaGUyovDkLaoukMPuQ55W7tD.jpeg', 'àn piano kawai GL-20 được thiết kế tỉ mỉ đến từng chi tiết có chất lượng âm thanh tuyệt vời. Piano Kawai GL-10 thuộc dòng \"baby grand\" được sản xuất và lắp ráp thủ công theo nguyên tắc cổ điển, sẽ là cây đàn phù hợp cho những cuộc thi âm nhạc', '<p>&agrave;n piano kawai&nbsp;GL-20&nbsp;được thiết kế tỉ mỉ đến từng chi tiết c&oacute; chất lượng &acirc;m thanh tuyệt vời.&nbsp;Piano Kawai GL-10 thuộc d&ograve;ng &quot;baby grand&quot; được sản xuất v&agrave; lắp r&aacute;p thủ c&ocirc;ng theo nguy&ecirc;n tắc cổ điển, sẽ l&agrave; c&acirc;y đ&agrave;n ph&ugrave; hợp cho những cuộc thi &acirc;m nhạc</p>', '<p>&agrave;n piano kawai&nbsp;GL-20&nbsp;được thiết kế tỉ mỉ đến từng chi tiết c&oacute; chất lượng &acirc;m thanh tuyệt vời.&nbsp;Piano Kawai GL-10 thuộc d&ograve;ng &quot;baby grand&quot; được sản xuất v&agrave; lắp r&aacute;p thủ c&ocirc;ng theo nguy&ecirc;n tắc cổ điển, sẽ l&agrave; c&acirc;y đ&agrave;n ph&ugrave; hợp cho những cuộc thi &acirc;m nhạc</p>', 3),
(9, 'Kawai K-800', 50000000, 'IFE6Fd6f3jMw7YKkOBMPD8QHRve9YkYhq6wY5AHO.png', 'Đàn piano Kawai K800 được biết đến là cây đàn cao cấp trong dòng K series của hãng Kawai, có thiết kế kiểu dáng thanh lịch, âm điệu chuẩn piano acoustic', '<p><strong>Đ&agrave;n Piano Kawai K800&nbsp;</strong>l&agrave; c&acirc;y đ&agrave;n piano cơ thuộc d&ograve;ng upright piano kawai K series của Nhật bản, thiết kế với kiểu d&aacute;ng thanh lịch, giai&nbsp;điệu&nbsp;ấm&nbsp;&aacute;p, ch&acirc;n thực. K800 được mệnh danh l&agrave; c&acirc;y đ&agrave;n piano Upright cao cấp, được rất nhiều giới nhạc c&ocirc;ng, nghệ sĩ đ&aacute;nh gi&aacute; cao v&agrave; lựa chọn.</p>\r\n\r\n<p><img alt=\"dan_piano_kawai_k800\" src=\"https://vietthuong.vn/images/File/Piano/dan_piano_kawai_k800.jpg\" /></p>\r\n\r\n<p>Những t&iacute;nh năng nỗi bật tr&ecirc;n đ&agrave;n piano Kawai K-800</p>\r\n\r\n<h3><strong>1. CẢM ỨNG B&Agrave;N PH&Iacute;M KAWAI K-800</strong></h3>\r\n\r\n<p><strong>Bộ cơ đ&agrave;n upright piano Millennium III</strong></p>\r\n\r\n<p><img alt=\"Kawai Piano Millennium III Upright Action\" src=\"https://lh5.googleusercontent.com/aw39UCtL9u2BeE_jKYKQKegUC69n_Gadm5E8UiUI0peYw0_qq7opNXD3IbsKzCi8ibZfG9DYuHjSBvVJBj9vcSM8W1Vd75ewaZuCE2RDIp9HljKE8Sc3_wGpFWAl-I9EVnIRqftn\" /></p>\r\n\r\n<p>C&aacute;c bộ phận của bộ cơ đ&agrave;n upright piano Millennium III được l&agrave;m bằng chất liệu ABS-Carbon, một loại vật liệu tổng hợp được tạo ra bởi sự pha trộn của sợi carbon v&agrave; nhựa ABS Styran. ABS-Carbon rất cứng chắc, cho ph&eacute;p c&aacute;c bộ phận của bộ cơ trở n&ecirc;n nhẹ nh&agrave;ng hơn m&agrave; kh&ocirc;ng cần phải chịu nhiều lực. Kết quả l&agrave; bộ cơ hoạt động mạnh hơn v&agrave; nhanh hơn, nhờ đ&oacute; mang lại hiệu quả cao hơn, khả năng kiểm so&aacute;t tốt hơn cũng như độ ổn định tuyệt vời hơn so với c&aacute;c loại bộ cơ truyền thống ho&agrave;n to&agrave;n bằng gỗ.</p>\r\n\r\n<p><strong>Bề mặt b&agrave;n ph&iacute;m NEOTEX</strong></p>\r\n\r\n<p><img alt=\"Kawai Piano NEOTEX\" src=\"https://lh4.googleusercontent.com/CVlPOKASgEgngboGU6gCsBEEHBM39YzCx0X5QJ1gEhSfX3PpdgvYyNqe3N8rPsk7yQSFj7-U2a5baqyMH5qSs-hXVywVy2Qv_-1TjvAVQqxrzSBVZRu8aiHTwb8OLsyU8CACzXr_\" /></p>\r\n\r\n<p>Vật liệu bề mặt b&agrave;n ph&iacute;m NEOTEX độc quyền của Kawai được l&agrave;m bằng sợi cellulose mang lại kết cấu tinh tế v&agrave; mượt m&agrave; của ng&agrave; voi v&agrave; gỗ mun tự nhi&ecirc;n, v&agrave; được phủ lớp bề mặt silica b&aacute;n xốp c&oacute; khả năng hấp thụ c&aacute;c loại dầu tự nhi&ecirc;n v&agrave; mồ h&ocirc;i tay c&oacute; thể g&acirc;y trượt ph&iacute;m. NEOTEX ngăn ngừa sự rạn nứt v&agrave; ngả m&agrave;u qua nhiều năm sử dụng, đồng thời mang lại &acirc;m thanh bổng v&uacute;t v&agrave; tự nhi&ecirc;n để cho cảm gi&aacute;c nhất qu&aacute;n tr&ecirc;n to&agrave;n bộ b&agrave;n ph&iacute;m.</p>\r\n\r\n<p><strong>Gi&aacute; đỡ bộ cơ bằng nh&ocirc;m đ&uacute;c</strong></p>\r\n\r\n<p><img alt=\"Piano Kawai Die-Cast Aluminum Action Brackets\" src=\"https://lh3.googleusercontent.com/3lQ1OCdiqgVeTA8euIEt00Xmkv8SrxRqkuGl-GMZ-N52MmWnkQCNEHglJgTACY-8EjRTtA9TYtgvTzUTi25uF5_36MfNJHC9MnfLYPDHrwjJIX6TiGxvyxgunQhZYfvB5I5w98n3\" /></p>\r\n\r\n<p>Piano Kawai được đ&uacute;c từ một khu&ocirc;n cố định, những bộ phận của gi&aacute; đỡ được đ&uacute;c ch&iacute;nh x&aacute;c v&agrave; giống hệt nhau trong mọi lần đ&uacute;c. D&ograve;ng piano K-Series c&oacute; 3 gi&aacute; đỡ bằng nh&ocirc;m đ&uacute;c để cung cấp lực đỡ chắc chắn, nhất qu&aacute;n v&agrave; ổn định cho b&agrave;n ph&iacute;m chuẩn x&aacute;c theo thời gian.</p>\r\n\r\n<p><strong>Ph&iacute;m đ&agrave;n d&agrave;i hơn</strong></p>\r\n\r\n<p><img alt=\"Piano Kawai Extended Key Length\" src=\"https://lh3.googleusercontent.com/IPqQGXwoFy87REFdCVAaMF5lAErGe7kEYu_emz71hphIYZvOppyOXSeSRpjgFhbpHAk_79kgCDAazV4xNibpnqV7TOHE_R2YLg54fQhToWjWMAte3pRGs7YYgnGvqKiRm8CGe1bW\" /></p>\r\n\r\n<p>Ph&iacute;m đ&agrave;n d&agrave;i hơn gi&uacute;p chơi đ&agrave;n dễ d&agrave;ng hơn, đồng thời gi&uacute;p bề mặt ph&iacute;m nhạy hơn khi chơi. Người chơi nhờ đ&oacute; c&oacute; thể kiểm so&aacute;t tốt hơn v&agrave; tr&igrave;nh diễn hiệu quả hơn với nỗ lực &iacute;t hơn.</p>\r\n\r\n<h3>2. &Acirc;M THANH KAWAI&nbsp;K-800</h3>\r\n\r\n<p>Bảng cộng hưởng (soundboard) được l&agrave;m bằng gỗ v&acirc;n sam nguy&ecirc;n tấm gọt thon</p>\r\n\r\n<p><img alt=\"Piano Kawai Soundboard\" src=\"https://lh4.googleusercontent.com/DtE3CI9dJUJgqzv8oQoTTj8vQTGtxVPv3L1CfmfMHeMYDXqQjerxYUSWE4CFdGSKTBBG450vQqBNZ3SLVacjjgVycvLQTuHGK4YlasoWjBaU-P6vctD0e-bxQowQLOhs47iGLK0j\" /></p>\r\n\r\n<p>Bảng cộng hưởng l&agrave; bộ phận quan trọng nhất của c&acirc;y đ&agrave;n piano. Bộ phận n&agrave;y được d&ugrave;ng để biến sự rung động của c&aacute;c d&acirc;y đ&agrave;n th&agrave;nh những &acirc;m vang phong ph&uacute;. Kawai chỉ sử dụng loại gỗ v&acirc;n sam nguy&ecirc;n tấm c&oacute; đường v&acirc;n gỗ thẳng được cưa l&agrave;m tư gh&eacute;p lại với nhau. Từng tấm gỗ được gọt thon lại để tối ưu h&oacute;a khả năng tạo ra &acirc;m thanh. Chỉ ri&ecirc;ng những điều ấy đ&atilde; đ&aacute;p ứng, thậm ch&iacute; vượt qu&aacute;, ti&ecirc;u chuẩn d&agrave;nh cho đ&agrave;n upright piano chuy&ecirc;n nghiệp.</p>\r\n\r\n<p><strong>Trụ đ&agrave;n piano Kawai chắc chắn chịu tốt lực cho khung đ&agrave;n.</strong></p>\r\n\r\n<p>C&aacute;c b&uacute;a đ&agrave;n c&oacute; ghim h&igrave;nh chữ T, l&otilde;i bằng gỗ gụ v&agrave; được bọc nỉ hai b&ecirc;n</p>\r\n\r\n<p><img alt=\"Piano Kawai hammers\" src=\"https://lh5.googleusercontent.com/DQJdCDEItCBwsqK3fQjLs4NkAOqvpVFtmgDc9vpRl4iOa8LkS8PbG9szgLcZJ4CAgxVjYTpwcXLKwf9GQRc7kCIjMmJUf1nzHzrqmF0xXEHwGZo12xZ-D3nfoBDgY9CuLEmSVaCP\" /></p>\r\n\r\n<p>B&uacute;a đ&agrave;n Piano Kawai K-800 được l&agrave;m bằng gỗ gụ c&oacute; trọng lượng nhẹ v&agrave; cực kỳ nhạy. Chỉ tr&ecirc;n những c&acirc;y đ&agrave;n chất lượng cao như d&ograve;ng upright piano chuy&ecirc;n nghiệp K-Series, b&uacute;a đ&agrave;n mới được l&agrave;m ho&agrave;n to&agrave;n bằng gỗ gụ. Tất cả b&uacute;a đ&agrave;n của d&ograve;ng K-Series đều c&oacute; ghim h&igrave;nh chữ T để giữ h&igrave;nh dạng b&uacute;a v&agrave; bọc th&ecirc;m một lớp nỉ để hỗ trợ việc tạo ra c&aacute;c &acirc;m thanh tuyệt vời.</p>\r\n\r\n<h3><strong>3. ĐỘ CHẮC CHẮN KAWAI&nbsp;K-800</strong></h3>\r\n\r\n<p><strong>Thanh đỡ ph&iacute;m (keyslip) được gia cố bằng th&eacute;p</strong></p>\r\n\r\n<p><img alt=\"Piano Kawai Steel-Reinforced Keyslip\" src=\"https://lh6.googleusercontent.com/c6AlwnDtQ-joWQHvUJncXrgzyTjRU_4cvL5k43blYvK2WClTJayV9jSjsSs1nAmEl4JTnWAMm8-N9ptLUC9D_g1jwpyAj3gZN7sosAFGhSjGl53NXcLFh9gWlKEkFsh0s1nZAJ4n\" /></p>\r\n\r\n<p>Thanh đỡ ph&iacute;m piano Piano Kawai K-600 l&agrave; một thanh gỗ d&agrave;i, nằm ngang, bao phủ mặt trước của c&aacute;c ph&iacute;m đ&agrave;n. Ch&iacute;nh v&igrave; được l&agrave;m bằng đoạn gỗ d&agrave;i v&agrave; mỏng m&agrave; thanh đỡ ph&iacute;m c&oacute; thể v&ecirc;nh l&ecirc;n hoặc bị uốn cong do sự thay đổi độ ẩm, g&acirc;y ra hiện tượng kẹt ph&iacute;m. Để khắc phục vấn đề n&agrave;y, một mặt của thanh đỡ ph&iacute;m được gia cố bằng c&aacute;ch bọc th&eacute;p d&agrave;y. Ph&iacute;m đ&agrave;n nhờ đ&oacute; sẽ di chuyển tự do hơn m&agrave; kh&ocirc;ng bị kẹt ph&iacute;m hay cạ ph&iacute;m.</p>\r\n\r\n<p><strong>Bệ đỡ ph&iacute;m (keybed) v&agrave; thanh nối (braces)</strong></p>\r\n\r\n<p><img alt=\"Piano Kawai Keybed and Braces\" src=\"https://lh3.googleusercontent.com/LsHww17cCxR9k0CVA_IPF-BgY1MJRcpmsRieEgEEG_2j-4Am_GS3s7Yuz-jy_13zbRgsI0jxoIOBbhsvIUVUe2OiRef6WB7OdtZPNYywbFM3WrxxobAjzbnUttWMF7fOG3gycgMl\" /></p>\r\n\r\n<p>Bệ đỡ ph&iacute;a dưới ph&iacute;m đ&agrave;n phải thật chắc chắn v&agrave; ổn định để hỗ trợ cho qu&aacute; tr&igrave;nh lắp r&aacute;p bộ cơ cũng như duy tr&igrave; sự điều chỉnh bộ cơ qua nhiều năm d&ugrave; cường độ sử dụng cao. Bệ đỡ ph&iacute;m của d&ograve;ng K-Series được l&agrave;m bằng những phiến gỗ cứng d&aacute;t mỏng nhưng chắc chắn v&agrave; được gia cố bằng thanh nối ph&iacute;a sau ở b&ecirc;n tr&ecirc;n khung th&eacute;p để gi&uacute;p đảm bảo vị tr&iacute; ổn định v&agrave; gia tăng độ chắc chắn nhằm tr&aacute;nh thất tho&aacute;t năng lượng.</p>\r\n\r\n<p>Thiết kế mới cho vẻ đẹp sang trọng, qu&yacute; ph&aacute;i v&agrave; cao cấp. B&aacute;nh xe đ&ocirc;i cao cấp được l&agrave;m từ đồng thau chắc chắn.</p>', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"2\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>K&iacute;ch thước&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Chiều cao</p>\r\n			</td>\r\n			<td>\r\n			<p>134 cm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Chiều rộng</p>\r\n			</td>\r\n			<td>\r\n			<p>153 cm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Chiều s&acirc;u</p>\r\n			</td>\r\n			<td>\r\n			<p>65 cm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Trọng lượng</p>\r\n			</td>\r\n			<td>\r\n			<p>284 kg</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>Độ nhạy&nbsp;</strong>&nbsp;&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>Bề mặt ph&iacute;m trắng</p>\r\n			</td>\r\n			<td>\r\n			<p>NEOTEX</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Bề mặt ph&iacute;m đen</p>\r\n			</td>\r\n			<td>\r\n			<p>NEOTEX</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Bộ m&aacute;y</p>\r\n			</td>\r\n			<td>\r\n			<p>Millennium III</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Nỉ b&uacute;a đ&agrave;n</p>\r\n			</td>\r\n			<td>\r\n			<p>C&oacute;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"6\">\r\n			<p><strong>&Acirc;m sắc&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Chiều d&agrave;i của d&acirc;y đ&agrave;n số 1</p>\r\n			</td>\r\n			<td>\r\n			<p>1,280 mm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Thanh Cutoff</p>\r\n			</td>\r\n			<td>\r\n			<p>2</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Agraffes</p>\r\n			</td>\r\n			<td>\r\n			<p>1-51 key</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Duplex scaling</p>\r\n			</td>\r\n			<td>\r\n			<p>Fore &amp; back</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Back posts</p>\r\n			</td>\r\n			<td>\r\n			<p>6</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>Thiết kế b&ecirc;n ngo&agrave;i&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><a href=\"https://vietthuong.vn/dac-diem-pedal-tren-dan-piano\">Pedal</a></p>\r\n			</td>\r\n			<td>\r\n			<p><a href=\"https://vietthuong.vn/dac-diem-pedal-tren-dan-piano\">Soft, Muffler, Damper</a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>B&aacute;nh xe</p>\r\n			</td>\r\n			<td>\r\n			<p>B&aacute;nh xe đ&ocirc;i, đồng thau</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Softfall</p>\r\n			</td>\r\n			<td>\r\n			<p>C&oacute;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Kh&oacute;a</p>\r\n			</td>\r\n			<td>\r\n			<p>C&oacute;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Kh&aacute;c</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Gi&aacute; nhạc</p>\r\n			</td>\r\n			<td>\r\n			<p>Grand</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 1),
(10, 'Campbell KC115D', 20000000, 'XVWYU0hnKSf85ub2WJOLumjCNbM0oZc3sjnlBBzm.png', 'Đàn Piano Kohler & Campbell là một trong 5 cây đàn piano mới giá rẻ bán chạy tại Việt Thương Music,  KC115D có thiết kế sang trọng, bảng cộng hưởng được làm từ g', '<p>Đ&agrave;n Piano Kohler &amp; Campbell l&agrave; một trong 5 c&acirc;y đ&agrave;n piano mới gi&aacute; rẻ b&aacute;n chạy tại Việt Thương Music, &nbsp;KC115D c&oacute; thiết kế sang trọng, bảng cộng hưởng được l&agrave;m từ gỗ v&acirc;n sam rắn chắc, được phủ một m&agrave;u đen huyền b&iacute;, k&iacute;ch thước nhỏ gọn với chiều cao 115 cm, &acirc;m thanh ấm rất th&iacute;ch hợp cho trẻ mới học đ&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-02.jpg\" /></p>\r\n\r\n<p>KC115D được thiết kế h&agrave;i h&ograve;a về kiểu d&aacute;ng cũng như c&ocirc;ng nghệ &acirc;m thanh, Soundboard được l&agrave;m bằng gỗ cao cấp, b&uacute;a đ&agrave;n được l&agrave;m bằng len hảo hạng c&ugrave;ng với những chi tiết kh&aacute;c được l&agrave;m từ những chất liệu cao cấp, đảm bảo cho chất &acirc;m cũng như độ bền của c&acirc;y đ&agrave;n.&nbsp;</p>\r\n\r\n<p>Hiện hệ thống Việt Thương Music đang l&agrave; nh&agrave; ph&acirc;n phối ch&iacute;nh thức &amp; độc quyền thương hiệu đ&agrave;n piano Kohler Campbell tại Việt Nam, d&ograve;ng sản phẩm KC115D tại Việt Thương Music nhập về với 2 m&agrave;u sắc ch&iacute;nh đ&oacute; l&agrave; m&agrave;u đen v&agrave; m&agrave;u gỗ mahogany.</p>\r\n\r\n<p>Một số h&igrave;nh ảnh về sản phẩm Koler Campbell KC-115D tại showroom Việt Thương Music:</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-10.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-02.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-03.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-05.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-06.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-07.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-08.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/san-pham/Kohler-Campbell/dan-piano-kohler-campbell-kc115-09.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>', '<table border=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"4\">K&iacute;ch thước</td>\r\n			<td>&nbsp;Chiều cao</td>\r\n			<td>&nbsp;115 cm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Chiều rộng</td>\r\n			<td>&nbsp;148 cm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Chiều ngang</td>\r\n			<td>&nbsp;55 cm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Trọng lượng</td>\r\n			<td>&nbsp;207 kg</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"5\">&nbsp; Độ nhạy&nbsp;</td>\r\n			<td>\r\n			<p>&nbsp;B&uacute;a đ&agrave;n</p>\r\n			</td>\r\n			<td>&nbsp;Len hảo hạng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;D&acirc;y đ&agrave;n</p>\r\n			</td>\r\n			<td>&nbsp;D&acirc;y Roslau</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;Soundboard</p>\r\n			</td>\r\n			<td>&nbsp;Tất cả sức căng bề mặt gỗ v&acirc;n sam</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Ph&iacute;m đ&agrave;n&nbsp;</td>\r\n			<td>&nbsp;Chiều d&agrave;i b&igrave;nh thường, c&acirc;n đối v&agrave; nặng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Bộ m&aacute;y</td>\r\n			<td>\r\n			<p>&nbsp;Tất cả l&agrave;m bằng gỗ c&oacute; thanh nh&ocirc;m</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"5\">&nbsp;Chi tiết b&ecirc;n trong đ&agrave;n</td>\r\n			<td>&nbsp;Pin block</td>\r\n			<td>\r\n			<p>&nbsp;Gỗ th&iacute;ch 15 lớp</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Chốt l&ecirc;n d&acirc;y</td>\r\n			<td>\r\n			<p>&nbsp;Mạ niken</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Xương sườn</td>\r\n			<td>&nbsp;Gỗ v&acirc;n sam h&igrave;nh chữ V</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Ngựa đ&agrave;n</td>\r\n			<td>&nbsp;Gỗ th&iacute;ch cứng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;Tấm đỡ</td>\r\n			<td>&nbsp;Tấm gang đ&uacute;c khu&ocirc;n&nbsp; c&aacute;t truyền thống</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"3\">&nbsp;Chi tiết b&ecirc;n ngo&agrave;i</td>\r\n			<td>&nbsp;Trụ chống</td>\r\n			<td>&nbsp;4 trụ gỗ cứng nhiều lớp</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;B&agrave;n đạp</td>\r\n			<td>&nbsp;Soft, Muffler, Damper</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;B&aacute;nh xe</td>\r\n			<td>&nbsp;B&aacute;nh kim loại đ&ocirc;i</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">&nbsp;M&agrave;u sắc</td>\r\n			<td>&nbsp;M&agrave;u đen (ebony)</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;M&agrave;u n&acirc;u (mahogany)</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;M&agrave;u gỗ &oacute;c ch&oacute; (walnut)</p>\r\n			</td>\r\n			<td>-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp; M&agrave;u Cherry</td>\r\n			<td>-</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', 1),
(11, 'Kawai GL-30', 76000000, 'Fv6IvZmD5ONkO4sxUx9by22ICfsa5o0OQGbU0N48.png', 'Đàn Piano Kawai GL-30 là sản phẩm đàn piano mới trong đại gia đình piano Kawai. Piano Kawai GL-30 thuộc dòng \"baby grand\" được sản xuất và lắp ráp thủ công theo nguyên tắc cổ điển.', '<p>Sự hoạt động ổn định trong khoảng thời gian d&agrave;i của độ cảm ứng ph&iacute;m ở GL-30 chỉ c&oacute; thể đạt được bằng việc đạt đến một thiết kế th&ocirc;ng minh c&ugrave;ng những phương ph&aacute;p v&agrave; vật liệu ti&ecirc;n tiến nhất. Loại đ&agrave;n n&agrave;y ph&ugrave; hợp cho những cuộc thi &acirc;m nhạc, biểu diễn v&agrave; cũng được kh&aacute; nhiều gia đ&igrave;nh chọn lựa để trưng b&agrave;y như một loại nội thất cao cấp kết hợp tập luyện &acirc;m nhạc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/kawai-gl-30-grand-piano-white.jpg\" /></p>\r\n\r\n<h2><strong>Bộ cơ đ&agrave;n piano&nbsp;Millennium III bằng ABS Carbon Với Sợi Carbon</strong></h2>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/Bo-co-dan-MillenniumIII_cua-kawai-GL-30.jpg\" /></p>\r\n\r\n<p>C&aacute;c bộ phận đặc biệt của bộ m&aacute;y cơ Millennium III Action được l&agrave;m bằng ABS - Carbon, một chất liệu tổng hợp được tạo ra bởi sự pha trộn của sợi carbon v&agrave; ABS styran &ndash; một chất liệu nổi tiếng của Kawai. ABS - Carbon l&agrave; chất liệu v&ocirc; c&ugrave;ng mạnh mẽ v&agrave; cứng c&aacute;p, cho ph&eacute;p tạo ra c&aacute;c bộ phận hoạt động nhẹ hơn m&agrave; kh&ocirc;ng phải hao tốn nhiều sức lực. Với chất liệu đặc biệt n&agrave;y, bộ m&aacute;y cơ hoạt động mạnh mẽ hơn, nhanh nhạy hơn v&agrave; mang lại những ưu điểm tuyệt vời: khả năng hoạt động vượt trội, sự kiểm so&aacute;t tốt hơn v&agrave; ổn định hơn so với bộ m&aacute;y cơ l&agrave;m bằng gỗ th&ocirc;ng thường.</p>\r\n\r\n<h2><strong>Thanh Đ&ograve;n Bẩy Carbon Độc Quyền</strong></h2>\r\n\r\n<p>Thanh đ&ograve;n bẩy h&igrave;nh L l&agrave; li&ecirc;n kết chủ chốt giữa ph&iacute;m đ&agrave;n v&agrave; b&uacute;a đ&agrave;n với chức năng kiểm so&aacute;t cường độ v&agrave; sự t&aacute;i lập hoạt động của ph&iacute;m đ&agrave;n. Thanh đ&ograve;n bẩy Carbon l&agrave; thiết kế độc quyền của Kawai với 2 chất liệu cấu th&agrave;nh: carbon v&agrave; polyacetal. Bộ phận đặc biệt n&agrave;y được đ&uacute;c như một chi tiết t&aacute;ch rời ri&ecirc;ng biệt với thiết kế &ldquo;L&otilde;i Kho&eacute;t Cho&atilde;i&rdquo; (Tapered Core) độc quyền của Kawai nhằm tối thiểu h&oacute;a trọng lượng, tối đa h&oacute;a sức mạnh định hướng v&agrave; tối ưu h&oacute;a sự phản ứng. Với kết cấu bền vững kh&oacute; ph&aacute; hủy n&agrave;y, thanh đ&ograve;n bẩy Carbon kh&ocirc;ng bị uốn cong khi độ ẩm thay đổi v&agrave; kh&ocirc;ng cần b&ocirc;i trơn; nhờ đ&oacute;, giảm nhu cầu bảo dưỡng. Một kết cấu với tiếp x&uacute;c bề mặt cực nhỏ sẽ gia tăng đ&aacute;ng kể sự kiểm so&aacute;t khi chơi những đoạn nhạc với cường độ nhẹ (pianissimo). V&igrave; vậy, với những thuộc t&iacute;nh đặc biệt n&agrave;y, thanh đ&ograve;n bẩy Carbon l&agrave; bước cải tiến vượt trội hơn hẳn so với những thanh đ&ograve;n bẩy l&agrave;m từ gỗ được kết nối bằng keo trong hầu hết những c&acirc;y đ&agrave;n piano th&ocirc;ng thường kh&aacute;c.</p>\r\n\r\n<h2><strong>Độ D&agrave;i Ph&iacute;m Được Tăng Th&ecirc;m</strong></h2>\r\n\r\n<p><strong><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/phim_piano_dai_hon_GL_30.jpg\" /></strong></p>\r\n\r\n<p>Gỗ v&acirc;n sam l&agrave; chất liệu l&yacute; tưởng cho ph&iacute;m đ&agrave;n &ndash; nhẹ, nhưng cực k&igrave; mạnh mẽ so với trọng lượng của n&oacute;. Gỗ v&acirc;n sam phản ứng lại với độ cong tối thiểu v&agrave; t&iacute;nh ổn định tuyệt vời &ndash; gi&uacute;p người chơi piano truyền tải từng sắc th&aacute;i tinh tế của những m&agrave;n tr&igrave;nh diễn trong khi đ&oacute; vẫn giữ được t&iacute;nh ổn định sau nhiều năm sử dụng khắc nghiệt. Độ d&agrave;i ph&iacute;m được tăng th&ecirc;m nhằm gi&uacute;p cho việc bấm ph&iacute;m dễ d&agrave;ng hơn v&agrave; thậm ch&iacute; tạo ra độ phản ứng tốt hơn từ gốc trong đến cạnh ngo&agrave;i của bề mặt hoạt động của ph&iacute;m đ&agrave;n. Thanh ph&iacute;m đ&agrave;n cũng được thiết kế n&acirc;ng cao hơn một ch&uacute;t so với cạnh đ&agrave;n nhằm tối đa h&oacute;a sự vững chắc; nhờ đ&oacute;, gi&uacute;p năng lượng khi bấm ph&iacute;m được truyền đi tốt hơn.</p>\r\n\r\n<h2><strong>Gi&aacute; Đỡ Bộ Cơ Bằng Nh&ocirc;m Đ&uacute;c &Aacute;p Lực</strong></h2>\r\n\r\n<p>Được l&agrave;m từ một khu&ocirc;n mẫu, những gi&aacute; đỡ bộ cơ đ&uacute;c bằng phương ph&aacute;p đ&uacute;c &aacute;p lực được định h&igrave;nh giống nhau một c&aacute;ch chuẩn x&aacute;c nhất. D&ograve;ng Grand Piano GX được thiết kế đặc trưng với 5 gi&aacute; đỡ bằng nh&ocirc;m đ&uacute;c &aacute;p lực đ&atilde; vạch ra th&ecirc;m một thước đo cho độ vững chắc v&agrave; t&iacute;nh ổn định của bộ m&aacute;y cơ; qua đ&oacute; đ&aacute;nh gi&aacute; được t&iacute;nh chuẩn x&aacute;c của độ cảm ứng ph&iacute;m theo thời gian. Thanh ngang cố định b&uacute;a đ&agrave;n (hammer rail) v&agrave; thanh ngang cố định khớp nối (wippen rail) được kh&oacute;a chặt vĩnh viễn nhằm duy tr&igrave; hệ dung sai chuẩn x&aacute;c của thiết kế bộ m&aacute;y cơ v&agrave; đảm bảo t&iacute;nh ổn định l&acirc;u d&agrave;i.</p>\r\n\r\n<h2><strong>Thanh Nh&ocirc;m Với Thiết Kế Dầm Đ&ocirc;i</strong></h2>\r\n\r\n<p>Thiết kế dầm đ&ocirc;i gi&uacute;p tăng cường độ mạnh v&agrave; t&iacute;nh ổn định của những thanh ngang của bộ m&aacute;y cơ nhằm hỗ trợ tuyệt đối cho b&uacute;a đ&agrave;n. Bề mặt răng cưa tinh vi của thanh ngang v&agrave; chất liệu Insuliner* độc quyền giữ cho m&eacute;p của c&aacute;c b&uacute;a đ&agrave;n chắc chắn hơn nhằm duy tr&igrave; sự sắp xếp chuẩn x&aacute;c của c&aacute;c b&uacute;a đ&agrave;n v&agrave; tối ưu h&oacute;a độ cảm ứng ph&iacute;m v&agrave; &acirc;m thanh.</p>\r\n\r\n<p>*Chất liệu được l&agrave;m từ cao su Neoprene tổng hợp. Nhằm cố định c&aacute;c b&uacute;a đ&agrave;n một c&aacute;ch chắc chắn, Kawai cho thiết kế một chất liệu mới &ndash; Insuliner để lắp đầy bề mặt khe r&atilde;nh ở r&igrave;a thanh ngang. Khi b&uacute;a đ&agrave;n được k&iacute;ch động sẽ sinh ra lực &eacute;p xuống thanh ngang. Tuy nhi&ecirc;n, lực &eacute;p của b&uacute;a đ&agrave;n kh&ocirc;ng thể t&aacute;c động l&ecirc;n chất liệu Insuliner. Nhờ đ&oacute;, c&aacute;c b&uacute;a đ&agrave;n vẫn cố định ở đ&uacute;ng vị tr&iacute; ban đầu.</p>\r\n\r\n<h2><strong>Bề Mặt Thanh Ngang Với Thiết Kế Răng Cưa</strong></h2>\r\n\r\n<p>To&agrave;n bộ bề mặt của thanh ngang cố định b&uacute;a đ&agrave;n được thiết kế răng cưa để giữ c&aacute;c m&eacute;p b&uacute;a đ&agrave;n chắc chắn nhằm tối ưu h&oacute;a trật tự c&aacute;c b&uacute;a đ&agrave;n v&agrave; tăng độ ch&iacute;nh x&aacute;c của b&uacute;a khi g&otilde; v&agrave;o d&acirc;y đ&agrave;n.</p>\r\n\r\n<h2><strong>Then C&agrave;i Ph&iacute;m Với Thiết Kế Của Đ&agrave;n Piano Giao Hưởng</strong></h2>\r\n\r\n<p>Việc sử dụng những then c&agrave;i ph&iacute;m được l&agrave;m từ gỗ th&iacute;ch rắn chắc l&agrave;m tăng th&ecirc;m độ cứng cho thanh ph&iacute;m đ&agrave;n nhằm truyền tải năng lượng đ&atilde; được chuyển h&oacute;a. Độ d&agrave;i then c&agrave;i ph&iacute;m của d&ograve;ng GL được tăng gấp đ&ocirc;i để tối đa h&oacute;a sự truyền tải năng lượng v&agrave; tối ưu h&oacute;a sức mạnh.</p>\r\n\r\n<h2><strong>Sức Mạnh Của Pianissimo</strong></h2>\r\n\r\n<p>Một trong những đặc t&iacute;nh quan trọng của một c&acirc;y đ&agrave;n piano tốt ch&iacute;nh l&agrave; phạm vi hoạt động &mdash; kh&ocirc;ng chỉ l&agrave; khả năng thể hiện ho&agrave;n hảo những đoạn nhạc mạnh (fortissimos)&nbsp;m&agrave; c&ograve;n l&agrave; khả năng truyền tải sắc th&aacute;i tinh tế v&agrave; nhạy cảm của những đoạn nhạc nhẹ (pianissimos). Những ưu thế c&ocirc;ng nghệ vượt bậc của Bộ M&aacute;y Cơ Thi&ecirc;n Ni&ecirc;n Kỉ Thế hệ thứ III (Millennium III Action) l&agrave; bằng chứng x&aacute;c thực nhất về khả năng đặc biệt để tạo ra những giai điệu nhẹ nh&agrave;ng ho&agrave;n hảo.&nbsp;</p>', '<p><strong>Th&ocirc;ng số kỹ thuật</strong></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>&nbsp; &nbsp; &nbsp; K&iacute;ch thước&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Chiều d&agrave;i</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>166 cm</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chiều rộng</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>152 cm</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chiều cao</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>102 cm</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Trọng lượng</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>312 kg</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>Độ nhạy&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Bề mặt ph&iacute;m trắng</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Acrylic</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Bề mặt ph&iacute;m đen</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Phenol</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Bộ m&aacute;y</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Millennium III</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Nỉ b&uacute;a đ&agrave;n</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>C&oacute;</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>&Acirc;m sắc&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>&nbsp; Bản cộng hưởng</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>&nbsp;Rắn chắc , gổ c&acirc;y t&ugrave;ng</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Agraffes</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>1-54 keys</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Duplex scaling</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Fore &amp; Back</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Back posts</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>3</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"4\">\r\n			<p><strong>Thiết kế b&ecirc;n ngo&agrave;i&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>&nbsp;Peart</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>&nbsp;Soft</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>B&aacute;nh xe</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>B&aacute;nh xe đơn, đồng thau</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lid&nbsp;props</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>2</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Softfall</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>C&oacute;</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Gi&aacute; nhạc</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Vị tr&iacute;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>5</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Hard Finish</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Kh&oacute;a</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>C&oacute;&nbsp;</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Kh&aacute;c</strong></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p><strong>Stretcher Over-Lap Integrated Design (SOLID)<br />\r\n			Longer Keys<br />\r\n			Steel-reinforced keyslip &amp; keybed<br />\r\n			V-pro plate</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 3),
(12, 'Cordoba Fusion  CD', 8000000, 'ieBWQMbYrAQQ0nZ1uORiKLXDvPkG5hbi7mTa6gOv.png', 'Cordoba 12 Natural  với mặt trước được làm từ gỗ Solid Western Red Cedar  nguyên tấm mặt trước, lưng và hông là gỗ Gụ Mahogany Châu Phi (Africa Mahogany), tích hợp EQ Fishman cho âm thanh ấm áp vang xa', '<p>Cordoba 12 Natural&nbsp; với mặt trước được l&agrave;m từ gỗ Solid Western Red Cedar&nbsp; nguy&ecirc;n tấm mặt trước, lưng v&agrave; h&ocirc;ng l&agrave; gỗ Gụ Mahogany Ch&acirc;u Phi (Africa Mahogany),&nbsp;t&iacute;ch hợp EQ Fishman cho &acirc;m thanh ấm &aacute;p vang xa</p>\r\n\r\n<p><img alt=\"\" src=\"https://vietthuong.vn/upload/content/images/cordoba/fusion-12-natural-cd.jpg\" /></p>', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">Series</th>\r\n			<td>Fusion</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Item Number</th>\r\n			<td>GUHYCOR05639</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Body Top</th>\r\n			<td>Solid Western Red Cedar</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Upper Bout Width</th>\r\n			<td>279mm (11&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Lower Bout Width</th>\r\n			<td>368mm (14 1/2&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Body Depth Upper Bout</th>\r\n			<td>85mm (3 1/3&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Body Depth Lower Bout</th>\r\n			<td>95mm (3 3/4&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Body Length</th>\r\n			<td>483mm (19&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Overall Length</th>\r\n			<td>978mm (38 1/2&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Soundhole Diameter</th>\r\n			<td>84mm (3 1/3&quot;)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Top Bracing Pattern</th>\r\n			<td>Fan</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Back and Sides Wood</th>\r\n			<td>Mahogany</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Top Binding</th>\r\n			<td>Pau Ferro</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Roset